import React, { useState, useEffect } from 'react';

function Filters({ onFilterChange, filters }) {
  const [localFilters, setLocalFilters] = useState(filters);

  // Sample locations and driver's licenses
  // In a real application, these would likely come from the API
  const locations = ['Berlin', 'Hamburg', 'Munich', 'Frankfurt', 'Cologne'];
  const driversLicences = ['CE', 'C', 'B', 'A'];

  // Update local filters when props change
  useEffect(() => {
    setLocalFilters(filters);
  }, [filters]);

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLocalFilters({
      ...localFilters,
      [name]: value
    });
  };

  // Apply filters
  const handleApplyFilters = (e) => {
    e.preventDefault();
    onFilterChange(localFilters);
  };

  // Reset filters
  const handleResetFilters = () => {
    const resetFilters = {
      location: '',
      driversLicence: ''
    };
    setLocalFilters(resetFilters);
    onFilterChange(resetFilters);
  };

  return (
    <div className="filters">
      <h2>Filter Applicants</h2>

      <form onSubmit={handleApplyFilters}>
        <div className="filter-group">
          <label htmlFor="location">Location:</label>
          <select
            id="location"
            name="location"
            value={localFilters.location}
            onChange={handleInputChange}
          >
            <option value="">All Locations</option>
            {locations.map(location => (
              <option key={location} value={location}>{location}</option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="driversLicence">Driver's License:</label>
          <select
            id="driversLicence"
            name="driversLicence"
            value={localFilters.driversLicence}
            onChange={handleInputChange}
          >
            <option value="">All Licenses</option>
            {driversLicences.map(licence => (
              <option key={licence} value={licence}>{licence}</option>
            ))}
          </select>
        </div>

        <div className="filter-actions">
          <button type="submit" className="apply-button">Apply Filters</button>
          <button type="button" onClick={handleResetFilters} className="reset-button">Reset</button>
        </div>
      </form>
    </div>
  );
}

export default Filters;
