import React, { useState, useEffect } from 'react';
import api from '../services/api';

function Filters({ onFilterChange, filters }) {
  const [localFilters, setLocalFilters] = useState(filters);
  const [locations, setLocations] = useState([]);
  const [driversLicences, setDriversLicences] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch filter options when component mounts
  useEffect(() => {
    const fetchFilterOptions = async () => {
      try {
        setLoading(true);

        // Fetch locations
        const locationsResponse = await api.getLocations();
        if (locationsResponse.success) {
          setLocations(locationsResponse.data);
        }

        // Fetch driver's licenses
        const licensesResponse = await api.getDriversLicences();
        if (licensesResponse.success) {
          setDriversLicences(licensesResponse.data);
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching filter options:', error);
        setLoading(false);
      }
    };

    fetchFilterOptions();
  }, []);

  // Update local filters when props change
  useEffect(() => {
    setLocalFilters(filters);
  }, [filters]);

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLocalFilters({
      ...localFilters,
      [name]: value
    });
  };

  // Apply filters
  const handleApplyFilters = (e) => {
    e.preventDefault();
    onFilterChange(localFilters);
  };

  // Reset filters
  const handleResetFilters = () => {
    const resetFilters = {
      location: '',
      driversLicence: ''
    };
    setLocalFilters(resetFilters);
    onFilterChange(resetFilters);
  };

  return (
    <div className="filters">
      <h2>Filter Applicants</h2>

      <form onSubmit={handleApplyFilters}>
        <div className="filter-group">
          <label htmlFor="location">Location:</label>
          <select
            id="location"
            name="location"
            value={localFilters.location}
            onChange={handleInputChange}
            disabled={loading}
          >
            <option value="">All Locations</option>
            {locations.map(location => (
              <option key={location} value={location}>{location}</option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="driversLicence">Driver's License:</label>
          <select
            id="driversLicence"
            name="driversLicence"
            value={localFilters.driversLicence}
            onChange={handleInputChange}
            disabled={loading}
          >
            <option value="">All Licenses</option>
            {driversLicences.map(licence => (
              <option key={licence} value={licence}>{licence}</option>
            ))}
          </select>
        </div>

        <div className="filter-actions">
          <button type="submit" className="apply-button" disabled={loading}>Apply Filters</button>
          <button type="button" onClick={handleResetFilters} className="reset-button" disabled={loading}>Reset</button>
        </div>
      </form>

      {loading && <div className="filter-loading">Loading filter options...</div>}
    </div>
  );
}

export default Filters;
